mapper.select:
        SELECT


--         级联表的字段
        ,yonghu.username as username
        ,yonghu.password as password
        ,yonghu.yonghu_name as      yonghuName
        ,yonghu.yonghu_phone as     yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.sex_types as sexTypes
        ,yonghu.yonghu_photo as yonghuPhoto

        FROM chuqin  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.yonghu_id = #{params.yonghuId}


                and a.today like CONCAT('%',#{params.today},'%')


                  and DATE_FORMAT(a.on_time, '%H:%i') >= #{params.onTimeStart}


                   and DATE_FORMAT(a.on_time, '%H:%i') <=  #{params.onTimeEnd}


                  and DATE_FORMAT(a.down_time, '%H:%i') >= #{params.downTimeStart}


                   and DATE_FORMAT(a.down_time, '%H:%i') <=  #{params.downTimeEnd} 


                and a.chuqin_types = #{params.chuqinTypes}


                  and a.overtimeNumber >= #{params.overtimeNumberStart}   


                  and a.overtimeNumber <= #{params.overtimeNumberEnd}   



                and yonghu.username like CONCAT('%',#{params.username},'%')


                and yonghu.password like CONCAT('%',#{params.password},'%')


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.bumen_types = #{params.bumenTypes}



                and yonghu.zhiwei_types = #{params.zhiweiTypes}



                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.sex_types = #{params.sexTypes}


                and yonghu.role_types = #{params.roleTypes}


                and yonghu.role_types != 3



        order by a.${params.orderBy} desc
